<form action="<?php echo base_url();?>configuration/editer_attribut/<?php echo $this->uri->segment(3);if($infoAttribut->CODE_ATTRIBUTE!=""){?>/<?php echo $infoAttribut->CODE_ATTRIBUTE;}?>" method="post">		       		<div class="form-group">		<label><?php echo $this->lang->line('domain-attribut-name');?> (*)</label>		<input class="form-control" name="NAME_ATTRIBUTE" value="<?php echo $infoAttribut->NAME_ATTRIBUTE ; ?>" required>	</div>	<div class="form-group">		<label><?php echo $this->lang->line('domain-attribut-description');?></label>		<input class="form-control" name="DESC_ATTRIBUTE" value="<?php echo $infoAttribut->DESC_ATTRIBUTE ; ?>">	</div>	<div class="form-group">		<label><?php echo $this->lang->line('domain-attribut-champ-cible');?> (*)</label>		<input class="form-control" name="FIELD_NAME" value="<?php echo $infoAttribut->FIELD_NAME ; ?>" required>	</div>	<div class="row">		<div class="col-sm-3">			<div class="form-group">				<label><?php echo $this->lang->line('domain-attribut-locked');?></label>				<div class="checkbox">						<label><input type="checkbox" name="FLG_LOCKED" value="1" <?php if($infoAttribut->FLG_LOCKED==1){?>checked="checked"<?php }?>></label>				</div>			</div>		</div>			<div class="col-sm-3">			<div class="form-group">				<label><?php echo $this->lang->line('domain-attribut-custom');?></label>				<div class="checkbox">						<label><input type="checkbox" name="FLG_CUSTOM" value="1" <?php if($infoAttribut->FLG_CUSTOM==1){?>checked="checked"<?php }?>></label>				</div>			</div>		</div>			<div class="col-sm-3">					<div class="form-group">				<label><?php echo $this->lang->line('domain-attribut-lov');?></label>				<div class="checkbox">						<label><input type="checkbox" name="FLG_LOV" value="1" <?php if($infoAttribut->FLG_LOV==1){?>checked="checked"<?php }?>></label>				</div>			</div>		</div>			<div class="col-sm-3">				<div class="form-group">				<label><?php echo $this->lang->line('domain-attribut-tkh');?></label>				<div class="checkbox">						<label><input type="checkbox" name="FLG_TKH" value="1" <?php if($infoAttribut->FLG_TKH==1){?>checked="checked"<?php }?>></label>				</div>			</div>		</div>	
    </div>    	<div class="form-group">    	<label><?php echo $this->lang->line('domain-attribut-type');?></label>    	<select class="form-control" name="TYPE_ATTRIBUTE" required>        <?php foreach ($listeTypes as $type) {		?>	        <option value="<?php echo $type->STRING_TYPE;?>"<?php if($type->STRING_TYPE == $infoAttribut->TYPE_ATTRIBUTE){echo ' selected="selected"';}?>><?php echo $type->NAME_TYPE;?></option>	    <?php           }?>        </select>	</div>    <div class="modal-footer">    	<div class="pull-left">(*) <?php echo $this->lang->line('champs-obligatoires');?></div>    	<button type="button" class="btn btn-default fenetre-close" data-dismiss="modal"><?php echo $this->lang->line('liste-close');?></button>        <button type="submit" class="btn btn-success" > <?php echo $this->lang->line('liste-valide');?></button>    </div></form>	