>",											"sInfoPostFix":    "",											"sLoadingRecords": "<?php echo $this->lang->line('datatable-loading');?>",										    "sZeroRecords":    "<?php echo $this->lang->line('datatable-empty');?>",											"sEmptyTable":     "<?php echo $this->lang->line('datatable-emptyTable');?>",											"oPaginate": {												"sFirst":      "<?php echo $this->lang->line('datatable-first');?>",												"sPrevious":   "<?php echo $this->lang->line('datatable-previous');?>",												"sNext":       "<?php echo $this->lang->line('datatable-next');?>",												"sLast":       "<?php echo $this->lang->line('datatable-lfas');?>"											},											"oAria": {												"sSortAscending":  "<?php echo $this->lang->line('datatable-asc');?>",												"sSortDescending": "<?php echo $this->lang->line('datatable-desc');?>"											}										},					"bStateSave": true,		        });			table.columns([4,5,6,7,8,9,10,11,12]).visible( false, false );	        table.columns([0,1,2,3]).visible( true, true );	        table.columns.adjust().draw( false );			 $('#table-sequences-steps').on('draw.dt', function ( e, settings) {					$(".mod-edit").unbind('click');					$(".mod-edit").click(function(){					var title = $(this).attr('data-title');					$.post($(this).attr('data-href'),{},function(retour){						$('#fenetre-edit').html(retour);						$('#fenetre-edit .fenetre-close').click(function(){							fenetre.dialog("close");						});						fenetre.dialog({title:title});						fenetre.dialog("open");					})					return false;					});				});			var fenetre = $('#fenetre-edit').dialog({	            autoOpen: false,	            resizable: true,	            modal: true,	            width: 750,	            draggable:true	          });			$(".mod-edit").click(function(){	            var title = $(this).attr('data-title');				$.post($(this).attr('data-href'),{},function(retour){					$('#fenetre-edit').html(retour);					$('#fenetre-edit .fenetre-close').click(function(){						fenetre.dialog("close");					});					fenetre.dialog({title:title});					fenetre.dialog("open");				})				return false;	        });			$('#btn-menu-cols').click(function(){	            if($('#menu-cols').is(':visible')){	            	$('#menu-cols').hide();	            }else{					$('#menu-cols').show();	            }	        });	        $(".check-cols").click(function(){				if($(this).is(':checked')){					$('#table-sequences-steps').DataTable().column($(this).val()).visible(true);				}else{					$('#table-sequences-steps').DataTable().column($(this).val()).visible(false);				}	        });		});		 /* Script pour empecher de fermer le dropdown menu apres un choix cliqu√© (Colonnes). */	    $('li.dropdown.special-dropdown a').on('click', function (event) {	    	$(this).parent().toggleClass("open");		});		$('body').on('click', function (e) {	    	if (!$('li.dropdown.special-dropdown').is(e.target) && $('li.dropdown.special-dropdown').has(e.target).length === 0 && $('.open').has(e.target).length === 0) {	        	$('li.dropdown.special-dropdown').removeClass('open');	    	}		});	</script> </body></html>