<form action="<?php echo base_url();?>donnees/editer_fichier_source_field/<?php echo $this->uri->segment(3);if($infoFichierSourceField->CODE_FIELD!=""){?>/<?php echo $infoFichierSourceField->CODE_FIELD;}?>" method="post">       	<?php if( $infoFichierSourceField->CODE_FIELD!=""){?>       	<div class="form-group">        	<label><?php echo $this->lang->line('fichier-source-fields-index');?> (*)</label>        	<input class="form-control" name="INDEX_FIELD" value="<?php echo $infoFichierSourceField->INDEX_FIELD ; ?>" required>        </div>        <?php }?>        <div class="form-group">        	<label><?php echo $this->lang->line('fichier-source-fields-name-field');?> (*)</label>        	<input class="form-control" name="NAME_FIELD" value="<?php echo $infoFichierSourceField->NAME_FIELD ; ?>" required>        </div>                <div class="form-group">        	<label><?php echo $this->lang->line('fichier-source-fields-type-field');?>  (*)</label>        	<select name="TYPE_FIELD" id="type_field" class="form-control" required>        		<option value=""></option>        		<?php foreach($listeTypes as $type){?>        			<option value="<?php echo $type->STRING_TYPE?>"<?php if($infoFichierSourceField->TYPE_FIELD==$type->STRING_TYPE){?> selected="selected"<?php }?>><?php echo $type->NAME_TYPE;?></option>        		<?php }?>        	</select>        </div>                 <div class="form-group champs-date" <?php if($infoFichierSourceField->CODE_FIELD=="" || $infoFichierSourceField->TYPE_FIELD!="d"){?>style="display:none;"<?php }?>>        	<label><?php echo $this->lang->line('fichier-source-fields-beg-hist');?></label>        	<div class="checkbox">        		<label><input type="checkbox" name="FLG_DAT_BEG_HIST" value="1" <?php if($infoFichierSourceField->FLG_DAT_BEG_HIST==1){?>checked="checked"<?php }?>></label>        	</div>        </div>        <div class="form-group champs-date" <?php if($infoFichierSourceField->CODE_FIELD=="" || $infoFichierSourceField->TYPE_FIELD!="d"){?>style="display:none;"<?php }?>>        	<label><?php echo $this->lang->line('fichier-source-fields-end-hist');?></label>        	<div class="checkbox">        		<label><input type="checkbox" name="FLG_DAT_END_HIST" value="1" <?php if($infoFichierSourceField->FLG_DAT_END_HIST==1){?>checked="checked"<?php }?>></label>       		</div>        </div>    <div class="modal-footer">    	<div class="pull-left">(*) <?php echo $this->lang->line('champs-obligatoires');?></div>    	<button type="button" class="btn btn-default fenetre-close" data-dismiss="modal"><?php echo $this->lang->line('liste-close');?></button>        <button type="submit" class="btn btn-success" > <?php echo $this->lang->line('liste-valide');?></button>    </div></form><script>	$(document).ready(function(){		$('#type_field').change(function(){			if($(this).val()=="d"){				$('.champs-date').show();			}else{				$('.champs-date').hide();			}		});	});</script>