<form action="<?php echo base_url();?>donnees/editer_champs_historique/<?php echo $this->uri->segment(3);if($infoHistoriqueChamps->CODE_FIELD!=""){?>/<?php echo $infoHistoriqueChamps->CODE_FIELD;}?>" method="post">       	<div class="form-group">        	<label><?php echo $this->lang->line('historique-champs-index');?> (*)</label>        	<input class="form-control" name="INDEX_FIELD" value="<?php echo $infoHistoriqueChamps->INDEX_FIELD ; ?>" required>        </div>       	<div class="form-group">        	<label><?php echo $this->lang->line('historique-champs-source');?> (*)</label>        	<input class="form-control" name="SOURCE_COLUMN" value="<?php echo $infoHistoriqueChamps->SOURCE_COLUMN ; ?>" required>        </div>        <div class="form-group">        	<label><?php echo $this->lang->line('historique-champs-destination');?></label>        	<input class="form-control" name="TARGET_COLUMN" value="<?php echo $infoHistoriqueChamps->TARGET_COLUMN ; ?>">        </div>		<div class="row">			<div class="col-md-6">				<div class="form-group">					<label><?php echo $this->lang->line('historique-champs-fusion');?></label>					<div class="checkbox">						<label><input type="checkbox" name="FLG_MERGE" value="1" <?php if($infoHistoriqueChamps->FLG_MERGE==1){?>checked="checked"<?php }?>></label>					</div>				</div>			</div>			<div class="col-md-6">				<div class="form-group">					<label><?php echo $this->lang->line('historique-champs-cle');?></label>					<div class="checkbox">						<label><input type="checkbox" name="FLG_KEY_FIELD" value="1" <?php if($infoHistoriqueChamps->FLG_KEY_FIELD==1){?>checked="checked"<?php }?>></label>					</div>				</div>			</div>		</div>         <div class="form-group">        	<label><?php echo $this->lang->line('historique-champs-type');?></label>        	<select name="TYPE_FIELD" class="form-control">        		<option value=""></option>        		<?php foreach($listeTypes as $type){?>        			<option value="<?php echo $type->STRING_TYPE?>"<?php if($infoHistoriqueChamps->TYPE_FIELD==$type->STRING_TYPE){?> selected="selected"<?php }?>><?php echo $type->NAME_TYPE;?></option>        		<?php }?>        	</select>        </div>    <div class="modal-footer">    	<div class="pull-left">(*) <?php echo $this->lang->line('champs-obligatoires');?></div>    	<button type="button" class="btn btn-default fenetre-close" data-dismiss="modal"><?php echo $this->lang->line('liste-close');?></button>        <button type="submit" class="btn btn-success" > <?php echo $this->lang->line('liste-valide');?></button>    </div></form>