<form action="<?php echo base_url();?>indicateurs/editer_paie_rubrique/<?php echo $CODE_INDICATOR;?>" id="form-select" method="post">	<input type="hidden" id="input-liste" name="listeRubriques" value=""/>	<div class="form-group">		<div class="connectedSortable" id="div-listeRubrique">			<h4><strong><?php echo $this->lang->line('indicateur-liste-rubriques');?></strong></h4>			<div class="form-group">        		<label><?php echo $this->lang->line('filtrer');?></label>        		<input class="form-control filtre-left" id="filtre-rubrique" name="filtre-rubrique" value="">        		<p class="help-block"><?php echo $this->lang->line('filtrer-elem');?></p>      	 	</div>			<ul id="listeRubrique" class="connectedSortable sortable-left">				<?php foreach($listeRubriques as $rubrique){					if(!in_array($rubrique->CODE_PR_ITEM,$listeRubriquesIndicateurPaieArray)){?>					<li id="<?php echo $rubrique->CODE_PR_ITEM;if($rubrique->CODE_PR_RULE!=NULL){echo "-".$rubrique->CODE_PR_RULE;}?>" data-val="<?php if($rubrique->NAME_PR_ITEM==""){echo strtolower($rubrique->CODE_PR_ITEM);}else{echo strtolower($rubrique->NAME_PR_ITEM);}?>">						<?php if($rubrique->NAME_PR_RULE!=NULL){echo "[".$rubrique->NAME_PR_RULE."] ";}if($rubrique->CODE_PR_ITEM!=""){echo $rubrique->CODE_PR_ITEM." | ";}echo $rubrique->NAME_PR_ITEM;?>					</li>				<?php }else{						$key=array_search($rubrique->CODE_PR_ITEM, $listeRubriquesIndicateurPaieArray);						if($listeRulesIndicateurPaieArray[$key]!=$rubrique->CODE_PR_RULE){?>							<li id="<?php echo $rubrique->CODE_PR_ITEM;if($rubrique->CODE_PR_RULE!=NULL){echo "-".$rubrique->CODE_PR_RULE;}?>" data-val="<?php if($rubrique->NAME_PR_ITEM==""){echo strtolower($rubrique->CODE_PR_ITEM);}else{echo strtolower($rubrique->NAME_PR_ITEM);}?>">								<?php if($rubrique->NAME_PR_RULE!=NULL){echo "[".$rubrique->NAME_PR_RULE."] ";}if($rubrique->CODE_PR_ITEM!=""){echo $rubrique->CODE_PR_ITEM." | ";}echo $rubrique->NAME_PR_ITEM;?>							</li>						<?php 						}					}				}?>			</ul>		</div>		<div class="sortable-selector">			<button class="btn btn-success go-to-right" title="<?php echo $this->lang->line('indicateur-paie-btn-select-toright');?>"><i class="fa fa-chevron-circle-right"></i></button>			<br/>			<button class="btn btn-success go-to-left" title="<?php echo $this->lang->line('indicateur-paie-btn-select-toleft');?>"><i class="fa fa-chevron-circle-left"></i></button>		</div>		<div class="connectedSortable" id="div-ListeRubriqueFusion">			<h4><strong><?php echo $this->lang->line('indicateur-liste-rubriques-paie');?></strong></h4>			<label><?php echo $this->lang->line('filtrer');?></label>			<input class="form-control filtre-right" id="filtre-rubrique-paie" name="filtre-rubrique-paie" value="">	        <p class="help-block"><?php echo $this->lang->line('filtrer-elem');?></p>			<ul id="ListeRubriquePaie" class="connectedSortable sortable-right">				<?php foreach($listeRubriquesIndicateurPaie as $rubrique){?>					<li id="<?php echo $rubrique->CODE_PR_ITEM;if($rubrique->CODE_PR_RULE!=NULL){echo "-".$rubrique->CODE_PR_RULE;}?>" data-val="<?php if($listeRubriquesArray[$rubrique->CODE_PR_ITEM]->NAME_PR_ITEM==""){echo strtolower($rubrique->CODE_PR_ITEM);}else{echo strtolower($listeRubriquesArray[$rubrique->CODE_PR_ITEM]->NAME_PR_ITEM);}?>">						<?php if($listeRulesArray[$rubrique->CODE_PR_RULE]->NAME_PR_RULE!=NULL){echo "[".$listeRulesArray[$rubrique->CODE_PR_RULE]->NAME_PR_RULE."] ";}if($listeRubriquesArray[$rubrique->CODE_PR_ITEM]->CODE_PR_ITEM!=""){echo $rubrique->CODE_PR_ITEM." | ";}echo $listeRubriquesArray[$rubrique->CODE_PR_ITEM]->NAME_PR_ITEM;?>					</li>				<?php }?>			</ul>		</div>	</div>	<div style="clear:both;"></div>	<br />	<div class="modal-footer">    	<button type="button" class="btn btn-default fenetre-close" data-dismiss="modal"><?php echo $this->lang->line('liste-close');?></button>        <button type="button" class="btn btn-success submit-form" > <?php echo $this->lang->line('liste-valide');?></button>    </div></form><script src="<?php echo URL_ASSETS;?>js/selectfilter.js"></script>